mapper.select:
SELECT 

FROM train_course_type a

WHERE a.id = #{id}

SELECT 

FROM train_course_type a

WHERE a.type = #{type} and a.stage = #{stage}

SELECT
FROM train_course_type a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.type = #{type}
AND a.stage = #{stage}
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM train_course_type a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE train_course_type SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
